INSERT INTO `articles` (`id`, `title`, `user_id`, `image`, `url`, `description`, `minitext`, `publish`, `articlevideo`, `ordered`, `reference`, `article_group_id`, `created_at`, `updated_at`) VALUES
(1, 'خصوصیات انواع پایه های مرکبات (قسمت اول)', 1, 1, 'خصوصیات-انواع-پایه-های-مرکبات-(قسمت-اول)', 'توضیحات مربوط به خصوصیات انواع پایه‌های مرکبات', 'تولید درختان مرکبات در طول صدها سال در کشورهای تولید کننده از نوع بذری بود. درختان بذری با داشتن دوره نونهالی طولانی، چندین سال فاقد میوه بوده و ضمن داشتن قدرت رشد عمومی زیاد، دارای خارهای زیادی نیز هستند.', 1, NULL, 1, NULL, 2, '2020-09-13 09:00:46', '2020-09-17 08:08:34'),
(2, 'خصوصیات پایه‌های مرکبات(قسمت دوم)', 1, 1, 'خصوصیات-پایههای-مرکباتقسمت-دوم', NULL, 'نارنج(C. aurantium L.) رایجترین پایه مرکبات در مناطق مرکبات‌خیز دنیا است. حساسیت این پایه به بیماری تریستزا باعث شده که از این پایه در کشورهای استرالیا، آرژانتین، برزیل، کالیفرنیا، اسپانیا، آمریکا و آفریقای جنوبی محدود شود.', 1, NULL, 2, NULL, 2, '2020-09-13 09:02:40', '2020-09-13 09:02:46'),
(3, 'سوسک حنایی خرما', 1, 1, 'سوسک-حنایی-خرما', NULL, 'سوسک حنایی خرما (نام علمی: Rhynchophorus ferrugineus)، یک گونه از سوسک‌های خرطوم‌دار که با عنوان سرخرطومی آسیایی یا شپشه نخل ساگو نیز شناخته شده‌است. سوسک‌های بالغ نسبتاً بزرگ هستند و طول بدن این حشرات حدود دو تا پنج سانتی‌متر می‌باشد و معمولاً یک رنگ هستند، اما بسیاری از آن‌ها رنگ قرمز آجری دارند و اغلب به عنوان گونه‌های مختلف (به عنوان مثال، Rhynchophorus vulneratus، که هنوز هم مورد اختلاف) ثبت شده‌اند.', 1, NULL, 3, NULL, 3, '2020-09-17 06:25:18', '2020-09-17 06:25:59'),
(4, 'بیماری موزائیک سیب و سبزردی (کلروز) بر اثر آهن', 1, 1, 'بیماری-موزائیک-سیب-و-سبزردی-(کلروز)-بر-اثر-آهن', NULL, 'بیماری موزائیک سیب و سبزردی (کلروز) بر اثر آهن: بارزترین نشانه‌های بیماری در بهار روی برگهای در حال رشد و نمو درختان آلوده سیب دیده می‌شود و...', 1, NULL, 4, NULL, 9, '2020-09-17 06:28:01', '2020-09-17 06:28:32'),
(5, 'بیماری چوب لاستیکی و پیچیدگی و تورم شاخه درختان سیب', 1, 1, 'بیماری-چوب-لاستیکی-و-پیچیدگی-و-تورم-شاخه-درختان-سیب', NULL, 'چوب لاستیکی به درختان سیب حمله می‌کند و به شکل آلودگی نهفته در گلابی دیده می‌شود. در نهالهای بذری درخت به، بافت مردگی پوست، شبیه شانکر ایجاد می‌کند.', 1, NULL, 5, NULL, 9, '2020-09-17 06:29:37', '2020-09-17 06:29:46'),
(6, 'بیماری سرطان طوقه و ریشه و جاروک سیب', 1, 1, 'بیماری-سرطان-طوقه-و-ریشه-و-جاروک-سیب', NULL, 'بیماری سرطان طوقه و ریشه که گال طوقه و ریشه هم نامیده می‌شود در خزانه‌ها، نهالستانها و باغ‌های میوه سراسر جهان شیوع دارد.', 1, NULL, 6, NULL, 9, '2020-09-17 06:30:41', '2020-09-17 06:30:54'),
(7, 'بیماری سوختگی آتشی سیب', 1, 1, 'بیماری-سوختگی-آتشی-سیب', NULL, 'سوختگی آتشی یکی از بیماریهای بسیار خطرناک باکتریای است که نخستین بار در نیویورک گزارش شد، سپس در اروپا و بسیاری از کشورهای میوه‌خیز شیوع پیدا کرد.', 1, NULL, 7, NULL, 9, '2020-09-17 06:31:43', '2020-09-17 06:31:54'),
(8, 'بیماری‌های پس از برداشت( پوسیدگی آلترناریایی،ریزوپوسی و فیتوفتورایی میوه سیب)', 1, 1, 'بیماری‌های-پس-از-برداشت(-پوسیدگی-آلترناریایی،ریزوپوسی-و-فیتوفتورایی-میوه-سیب)', NULL, 'میوه‌های سیب و گلابی به بیماری پوسیدگی آلترناریایی مبتلا می‌شوند.کم و بیش در همه کشورهایی که سیب و گلابی کشت می‌شوند، پوسیدگی آلترناریایی شایع است....', 1, NULL, 8, NULL, 9, '2020-09-17 06:32:53', '2020-09-17 06:33:18'),
(9, 'بیماری‌های پس از برداشت سیب (پوسیدگی کپک آبی و خاکستری سیب)', 1, 1, 'بیماری‌های-پس-از-برداشت-سیب-(پوسیدگی-کپک-آبی-و-خاکستری-سیب)', NULL, 'پوسیدگی کپک آبی از جمله بیماری‌هایی است که پس از برداشت سیب به وجود می‌آید. کپک آبی که پوسیدگی نرم یا پوسیدگی مرطوب هم نامیده می‌شود، مهم‌ترین بیماری پس از برداشت سیب است.', 1, NULL, 9, NULL, 9, '2020-09-17 06:34:58', '2020-09-17 06:35:18'),
(10, 'بیماری پوسیدگی آرمیلاریایی ریشه و پوسیدگی فیتوفتورایی طوقه، یقه و ریشه', 1, 1, 'بیماری-پوسیدگی-آرمیلاریایی-ریشه-و-پوسیدگی-فیتوفتورایی-طوقه،-یقه-و-ریشه', NULL, 'به پوسیدگی آرمیلاریایی ریشه،« بیماری قارچی ریشه بلوط»، « پوسیدگی ریشه بر اثر قارچ کلاهکدار» و پوسیدگی بند کفشی» هم گفته می‌شود....', 1, NULL, 10, NULL, 9, '2020-09-17 06:36:29', '2020-09-17 06:37:51'),
(11, 'بیماری‌های شانکر بوتریوسفریایی،نکتریایی و پوسیدگی رزلینیایی ریشه درختان سیب', 1, 1, 'بیماری‌های-شانکر-بوتریوسفریایی،نکتریایی-و-پوسیدگی-رزلینیایی-ریشه-درختان-سیب', NULL, 'شانکر بوتریوسفریایی سیب بیشتر در مناطقی با هوای مرطوب و بارندگی زیاد دیده می‌شود، اما در باغهای سیب واقع در دشتها و ارتفاعات با هوای سرد و خشک، شایع نیست...', 1, NULL, 11, NULL, 9, '2020-09-17 06:41:41', '2020-09-17 06:41:56'),
(12, 'آفت‌های درخت انجیر', 1, 1, 'آفت‌های-درخت-انجیر', NULL, 'یکی از مسائل عمده درختان انجیر در باغها و تک درخت‌های انجیر در باغچه‌ها و منازل، ریزش میوه‌ها قبل از رسیدن است. عدم تلقیح گلها توسط زنبور بلاستوفاگا یکی از عوامل ریزش می‌باشد. زنبور بلاستوفاگا ...', 1, NULL, 12, NULL, 6, '2020-09-17 06:43:26', '2020-09-17 06:43:37'),
(13, 'درخت انجیر و بیماری‌های آن', 1, 1, 'درخت-انجیر-و-بیماری‌های-آن', NULL, 'انجیر با نام علمی Ficus carica L درختی خزاندار، از تیره توت سانان است. در جنس Ficus حدود ۶۰۰ گونه وجود دارد که بیشتر گرمسیری و همیشه سبز هستند...', 1, NULL, 13, NULL, 6, '2020-09-17 06:44:29', '2020-09-17 06:44:42'),
(14, 'آفات مهم درخت انار (کرم گلوگاه انار،شته سبز و کنه انار)', 1, 1, 'آفات-مهم-درخت-انار-(کرم-گلوگاه-انار،شته-سبز-و-کنه-انار)', NULL, 'کرم گلوگاه انار در ایران  برروی انار دیده می‌شود ولی در کشورهای دیگر میزبان‌های متعدد مانند خرنوب، پرتقال، تمر هندی، ازگیل ژاپنی، بادام زمینی، انجیر، گردو، فندق، خرما، شاه بلوط، سیب، به، هلو، زردآلو و غیره دارد.', 1, NULL, 14, NULL, 8, '2020-09-17 06:45:44', '2020-09-17 06:45:50'),
(15, 'درخت انار و بیماری‌های آن', 1, 1, 'درخت-انار-و-بیماری‌های-آن', NULL, 'درخت انار با نام علمی Punica granatum به تیره انارسانان تعلق دارد.این درخت بومی آسیای مرکزی و غربی است و سالیان طولانی است که در ایران کاشته می‌شود...', 1, NULL, 15, NULL, 8, '2020-09-17 06:46:30', '2020-09-17 06:46:42'),
(16, 'آفات درخت زیتون (پسیل، سپردار بنفش و شپشک سیاه زیتون)', 1, 1, 'آفات-درخت-زیتون-(پسیل،-سپردار-بنفش-و-شپشک-سیاه-زیتون)', NULL, 'پسیل زیتون در اوایل بهار در هنگام رویش شاخه‌های جوان و بازشدن گل‌های زیتون بیشترین خسارت را باعث می‌شود.', 1, NULL, 16, NULL, 7, '2020-09-17 06:47:39', '2020-09-17 06:47:50'),
(17, 'درخت زیتون و بیماری‌های آن', 1, 1, 'درخت-زیتون-و-بیماری‌های-آن', NULL, 'زیتون با نام علمی Olea europea L.  درختی از تیره Oleaceae است. در جنس زیتون چندین گونه مختلف وجود دارد که از بین آن‌ها، تنها درخت زیتون میوه خوراکی تولید می‌کند و بقیه زینتی هستند.', 1, NULL, 17, NULL, 7, '2020-09-17 06:48:40', '2020-09-17 06:48:52'),
(18, 'آفات درخت خرما', 1, 1, 'آفات-درخت-خرما', NULL, 'سپردار سفید خرما (Parlatoria blanchardi)به نخل زینتی و درختان خرمای جوان مخصوصا درختانی که در کنار نهرها و جاهای مرطوب و همچنین پاجوش‌ها خسارت می‌زند...', 1, NULL, 18, NULL, 5, '2020-09-17 06:49:39', '2020-09-17 06:49:50'),
(19, 'بیماری‌های درخت خرما (زگیل سیاه، پوسیدگی گل آذین و خشکیدگی خوشه خرما)', 1, 1, 'بیماری‌های-درخت-خرما-(زگیل-سیاه،-پوسیدگی-گل-آذین-و-خشکیدگی-خوشه-خرما)', NULL, 'زگیل سیاه درخت خرما که به آن لکه گرافیولایی برگ نیز گفته می‌شود، شایع ترین بیماری نخل است، و در مناطقی که شرایط اقلیمی آن مرطوب است بروز می‌کند. و..', 1, NULL, 19, NULL, 5, '2020-09-17 06:50:35', '2020-09-17 06:50:44');

